package buddy_strings

import "testing"

func TestBuddyStrings(t *testing.T) {

	tests := []struct {
		name     string
		input    []string
		expected bool
	}{
		{name: "test1", input: []string{"ab", "ba"}, expected: true},
		{name: "test2", input: []string{"ab", "ab"}, expected: false},
		{name: "test3", input: []string{"aa", "aa"}, expected: true},
		{name: "test4", input: []string{"aaaaaaabc", "aaaaaaacb"}, expected: true},
		{name: "test4", input: []string{"abcd", "abcd"}, expected: false},
		{name: "test5", input: []string{
			"",
			""},
			expected: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			output := buddyStrings(tt.input[0], tt.input[1])
			if output != tt.expected {
				t.Errorf("buddyStrings = %v, want %v", output, tt.expected)
			}
		})
	}
}
